mapper.select:
    SELECT * FROM dbo.PMLS_JsStatement_Dtl r
	where r.delFlag=0 and r.jssId = #{jssId,jdbcType=INTEGER}
	ORDER BY r.id

	    SELECT cbc.id as comParentId,cbp.id as proParentId FROM dbo.Cash_Bill_Company cbc
  		INNER JOIN dbo.Cash_Bill_Project cbp ON cbc.proParentId = cbp.id AND cbp.delFlag = '0'
  		WHERE cbc.delFlag=0 AND cbc.cashBillNo=#{cashBillNo,jdbcType=VARCHAR}
